import{_ as e,c as d,o,a as c}from"./app.e6cc23d8.js";const A=JSON.parse('{"title":"\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u6B64\u6B21\u6325\u624B","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u6B64\u6B21\u6325\u624B","slug":"\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u6B64\u6B21\u6325\u624B"},{"level":3,"title":"\u4E09\u6B21\u63E1\u624B","slug":"\u4E09\u6B21\u63E1\u624B"},{"level":3,"title":"\u56DB\u6B21\u6325\u624B","slug":"\u56DB\u6B21\u6325\u624B"}],"relativePath":"articles/\u7F51\u7EDC/\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u56DB\u6B21\u6325\u624B.md","lastUpdated":1657766877000}'),a={name:"articles/\u7F51\u7EDC/\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u56DB\u6B21\u6325\u624B.md"},l=c('<h2 id="\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u6B64\u6B21\u6325\u624B" tabindex="-1">\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u6B64\u6B21\u6325\u624B <a class="header-anchor" href="#\u7B80\u5355\u7406\u89E3\u4E09\u6B21\u63E1\u624B\u4E0E\u6B64\u6B21\u6325\u624B" aria-hidden="true">#</a></h2><h3 id="\u4E09\u6B21\u63E1\u624B" tabindex="-1">\u4E09\u6B21\u63E1\u624B <a class="header-anchor" href="#\u4E09\u6B21\u63E1\u624B" aria-hidden="true">#</a></h3><p>\u6709\u4E00\u4E2A\u5BA2\u6237\u7AEF(clint),\u8FD8\u6709\u4E00\u4E2A\u670D\u52A1\u7AEF(server),\u4ED6\u4EEC\u8981\u8FDB\u884C\u4E00\u4E9B\u6570\u636E\u4EA4\u4E92, \u4F46\u5728\u8FDB\u884C\u6570\u636E\u4EA4\u4E92\u524D\u5B83\u4EEC\u53CC\u65B9\u90FD\u662F\u5904\u4E8E\u4E00\u4E2A\u5173\u95ED\u7684\u72B6\u6001(closed),\u6B64\u65F6\u6709\u5BA2\u6237\u7AEF\u53D1\u8D77\u4E00\u4E2A\u8BF7\u6C42,\u518D\u53D1\u9001\u8BF7\u6C42\u4E4B\u524D\u5C31\u4F1A\u5148\u8FDB\u884C\u4E09\u6B21\u63E1\u624B,\u4E92\u76F8\u786E\u8BA4\u5BF9\u65B9\u7684\u53D1\u9001\u4E0E\u63A5\u6536\u80FD\u529B.</p><ol><li>\u7B2C\u4E00\u6B21\u63E1\u624B \u9996\u5148\u5BA2\u6237\u7AEF\u53D1\u9001\u4E00\u4E2A<code>SYN</code>\u62A5\u6587()</li><li>\u7B2C\u4E8C\u6B21\u63E1\u624B \u6B64\u65F6\u670D\u52A1\u7AEF\u63A5\u6536\u5230\u4E4B\u540E\u4F1A\u77E5\u9053,\u8BF6,\u8FD9\u4E2A\u5BA2\u6237\u7AEF\u6709\u53D1\u9001\u7684\u80FD\u529B,\u6211\u4E5F\u80FD\u6536\u5230\u4ED6\u7ED9\u6211\u53D1\u7684\u4FE1\u606F,\u90A3\u6211\u8981\u56DE\u5E94\u4E00\u4E0B\u4ED6,\u7136\u540E\u670D\u52A1\u7AEF\u4F1A\u7ED9\u5BA2\u6237\u7AEF\u53D1\u9001\u4E00\u4E2A<code>SYN + ACK </code>\u62A5\u6587</li><li>\u7B2C\u4E09\u6B21\u63E1\u624B \u6B64\u65F6\u5BA2\u6237\u7AEF\u63A5\u6536\u5230\u670D\u52A1\u7AEF\u53D1\u7684\u62A5\u6587\u4E4B\u540E\u4F1A\u518D\u7ED9\u670D\u52A1\u7AEF\u53D1\u9001\u4E00<code>ACK</code>\u62A5\u6587,\u8868\u793A\u6211\u6536\u5230\u4F60\u7ED9\u6211\u53D1\u9001\u7684\u62A5\u6587\u4E86,\u54B1\u4FE9\u53EF\u4EE5\u5EFA\u7ACB\u8FDE\u63A5\u4E86</li></ol><p>\u4E09\u6B21\u63E1\u624B\u4E4B\u540E\u670D\u52A1\u7AEF\u4E0E\u5BA2\u6237\u7AEF\u5EFA\u7ACB\u8FDE\u63A5\u5C31\u53EF\u4EE5\u5F00\u59CB\u901A\u4FE1\u4E86,\u6709\u65F6\u4F1A\u8BF4\u6211\u7ED9\u4F60\u53D1\u4E00\u904D,\u4F60\u7ED9\u6211\u53D1\u4E00\u904D\u4E0D\u5C31\u5B8C\u4E86,\u5E72\u561B\u8FD9\u4E48\u9EBB\u70E6,\u5176\u5B9E\u7B80\u5355\u6765\u8BF4\u5C31\u662F\u4E3A\u4E86\u4E92\u76F8\u786E\u8BA4\u5BF9\u65B9\u7684\u63A5\u53D7\u80FD\u529B\u4E0E\u53D1\u9001\u80FD\u529B\u662F\u5426\u6B63\u5E38\u548C\u5EFA\u7ACB\u4E00\u4E2A\u53EF\u4FE1\u7684\u53CC\u5411\u4F20\u8F93,\u800C\u4E00\u822C\u7684\u63E1\u624B\u4E24\u6B21\u53EA\u80FD\u5EFA\u7ACB\u4E00\u4E2A\u5355\u5411\u4F20\u8F93</p><h3 id="\u56DB\u6B21\u6325\u624B" tabindex="-1">\u56DB\u6B21\u6325\u624B <a class="header-anchor" href="#\u56DB\u6B21\u6325\u624B" aria-hidden="true">#</a></h3><p>\u6B64\u65F6\u5BA2\u6237\u7AEF\u4E0E\u670D\u52A1\u7AEF\u7684\u901A\u4FE1\u7ED3\u675F\u4E4B\u540E,\u4F1A\u7ECF\u5386\u4E00\u4E2A\u56DB\u6B21\u6325\u624B\u7684\u9636\u6BB5</p><ol><li><p>\u7B2C\u4E00\u6B21\u6325\u624B</p><p>\u8BF6,\u8FD9\u6B21\u8BF7\u6C42\u6211\u5B8C\u4E8B\u4E86,\u90A3\u5BA2\u6237\u7AEF\u5148\u7ED9\u670D\u52A1\u7AEF\u53D1\u9001\u4E00\u4E2A<code>FIN</code>\u7684\u7EC8\u6B62\u62A5\u6587,\u5E76\u4E14\u505C\u6B62\u6570\u636E\u53D1\u9001,\u8FDB\u5165\u4E00\u4E2A<code>FIN_WAIT1</code>(\u7EC8\u6B62\u7B49\u5F85)\u7684\u72B6\u6001</p></li><li><p>\u7B2C\u4E8C\u6B21\u6325\u624B</p><p>\u670D\u52A1\u7AEF\u5728\u63A5\u6536\u5230<code>FIN1</code>\u62A5\u6587\u540E,\u77E5\u9053\u4E86\u54E6\u5BA2\u6237\u7AEF\u5B8C\u4E8B\u4E86\u554A,\u90A3\u884C\u5427,\u4E0D\u7ED9\u4ED6\u53D1\u6570\u636E\u4E86,\u5E76\u7ED9\u5BA2\u6237\u7AEF\u53D1\u4E00\u4E2A<code>ACK</code>\u7684\u62A5\u6587\u786E\u8BA4\u4E00\u4E0B,\u7136\u540E\u5C31\u8FDB\u5165<code>CLOSE_WAIT</code>(\u5173\u95ED\u7B49\u5F85)\u9636\u6BB5</p></li><li><p>\u7B2C\u4E09\u6B21\u6325\u624B</p><p>\u6B64\u65F6,\u670D\u52A1\u7AEF\u8981\u7ED3\u675F TCP \u901A\u4FE1\u4E86,\u4ED6\u5F97\u901A\u77E5\u5BA2\u6237\u7AEF\u4E00\u4E0B\u554A,\u5C31\u548C\u7B2C\u4E00\u6B21\u6325\u624B\u4E00\u6837,\u53D1\u4E2A<code>FIN</code>\u62A5\u6587\u7ED9\u5BA2\u6237\u7AEF,\u5E76\u4E14\u8FDB\u5165\u4E00\u4E2A<code>LAST_ACK </code>(\u6700\u540E\u786E\u8BA4) \u7684\u9636\u6BB5</p></li><li><p>\u7B2C\u56DB\u6B21\u6325\u624B</p><p>\u5F53\u5BA2\u6237\u7AEF\u6536\u5230<code>FIN</code>\u62A5\u6587\u540E,\u5C31\u77E5\u9053\u4E86\u670D\u52A1\u7AEF\u8981\u7ED3\u675F\u8FD9\u6B21 TCP \u901A\u4FE1\u4E86,\u884C,\u90A3\u548C\u4EBA\u5BB6\u8BF4\u4E00\u58F0,\u53D1\u4E2A<code>ASK</code>\u62A5\u6587\u901A\u77E5\u4E00\u4E0B,\u5E76\u4E14\u8FDB\u5165<code>TIME_WAIT </code>(\u65F6\u95F4\u7B49\u5F85)\u9636\u6BB5,\u6B64\u65F6\u670D\u52A1\u6BB5\u6536\u5230\u62A5\u6587\u540E,ok,\u5173\u4E86\u5427,\u6B64\u65F6\u670D\u52A1\u7AEF\u8FDB\u5165<code>closed</code>\u72B6\u6001,\u5173\u95ED\u6B64\u6B21 TCP \u901A\u4FE1,\u4F46\u662F\u5BA2\u6237\u7AEF\u8981\u7B49\u7740 TCP \u91CA\u653E\u6389\u624D\u4F1A\u8FDB\u5165<code>closed</code>\u72B6\u6001</p></li></ol>',8),t=[l];function i(_,r,s,p,n,h){return o(),d("div",null,t)}var u=e(a,[["render",i]]);export{A as __pageData,u as default};
